create table phpbb_acl_options
(
    auth_option_id INTEGER
        primary key autoincrement,
    auth_option    VARCHAR(50)      default ''  not null,
    is_global      INTEGER UNSIGNED default '0' not null,
    is_local       INTEGER UNSIGNED default '0' not null,
    founder_only   INTEGER UNSIGNED default '0' not null
);

create unique index phpbb_acl_options_auth_option
    on phpbb_acl_options (auth_option);

