create table phpbb_modules
(
    module_id       INTEGER
        primary key autoincrement,
    module_enabled  INTEGER UNSIGNED default '1' not null,
    module_display  INTEGER UNSIGNED default '1' not null,
    module_basename VARCHAR(255)     default ''  not null,
    module_class    VARCHAR(10)      default ''  not null,
    parent_id       INTEGER UNSIGNED default '0' not null,
    left_id         INTEGER UNSIGNED default '0' not null,
    right_id        INTEGER UNSIGNED default '0' not null,
    module_langname VARCHAR(255)     default ''  not null,
    module_mode     VARCHAR(255)     default ''  not null,
    module_auth     VARCHAR(255)     default ''  not null
);

create index phpbb_modules_class_left_id
    on phpbb_modules (module_class, left_id);

create index phpbb_modules_left_right_id
    on phpbb_modules (left_id, right_id);

create index phpbb_modules_module_enabled
    on phpbb_modules (module_enabled);

