create table phpbb_search_wordlist
(
    word_id     INTEGER
        primary key autoincrement,
    word_text   VARCHAR(255)     default ''  not null,
    word_common INTEGER UNSIGNED default '0' not null,
    word_count  INTEGER UNSIGNED default '0' not null
);

create index phpbb_search_wordlist_wrd_cnt
    on phpbb_search_wordlist (word_count);

create unique index phpbb_search_wordlist_wrd_txt
    on phpbb_search_wordlist (word_text);

